VALUES(33, '4gh22bdad3', 'Fresh Pear', 'user_1/rice-noddels.png', '<ul><li>Thin noodles made from rice flour.</li><li>Versatile, used in many Asian dishes.</li></ul>', 1.50, 2.75, '<ul><li><strong>Base:</strong> Rice flour, water.</li><li><strong>Texture:</strong> Chewy, soft.</li><li><strong>Forms:</strong> Flat, thin, wide.</li><li><strong>Color:</strong> White.</li></ul>', 'Organic', '10', '100 Days', '2025-04-03 13:30:06.000', 'published', true, true, true, false, 'sku6877', '2025-04-03 13:30:41.280', '2025-04-03 13:30:17.000', 7, 1, 1);
