VALUES(35, '5hcah235f4', 'Star Anise', 'user_1/star_anise.png', '<ul><li>Star-shaped spice with a licorice-like flavor.</li><li>Dried fruit of the Illicium verum tree.</li></ul>', 4.00, 5.00, '<ul><li><strong>Shape:</strong> 6-8 pointed star.</li><li><strong>Flavor:</strong> Licorice, anise, slightly sweet.</li><li><strong>Aroma:</strong> Strong, pungent.</li><li><strong>Use:</strong> Culinary, medicinal.</li></ul>', 'Organic', '9790', '100 Days', '2025-04-03 13:51:04.000', 'published', true, true, true, false, 'sku1510', '2025-04-03 13:51:31.947', '2025-04-03 13:51:18.000', 4, 1, 2);
