VALUES(42, 'gbd55ade2a', 'Daikon Radish', 'user_1/daikon-radish.png', '<ul><li>Long, white root vegetable with a mild, slightly peppery flavor.</li><li>Commonly used in Asian cuisines.</li></ul>', 1.90, 3.00, '<ul><li><strong>Shape:</strong> Long, cylindrical.</li><li><strong>Flavor:</strong> Mild, slightly peppery, sweet.</li><li><strong>Texture:</strong> Crisp, juicy.</li><li><strong>Color:</strong> White skin and flesh.</li></ul>', 'Organic', '98778', '20 Days', '2025-04-03 14:25:42.000', 'published', true, true, true, false, 'sku3826', '2025-04-03 14:26:05.140', '2025-04-03 14:26:00.000', 4, 1, 6);
